INSERT INTO WEAPON (name, type, acquisition_method, image_url) VALUES ('终夜', '手枪', '游戏一开始从商店以0元购入', 'https://placehold.co/600x400/ff3e00/ffffff?text=%E7%BB%88%E5%A4%9C');
INSERT INTO WEAPON (name, type, acquisition_method, image_url) VALUES ('芬里尔', '冲锋枪', '支线任务-全金属和尚，游戏一开始就能接，位置在北区码头附近的黄色问号，武器在任务要救的和尚旁边的桌子上。', 'https://placehold.co/600x400/fcee09/000000?text=%E8%8A%AC%E9%87%8C%E5%B0%94');
INSERT INTO WEAPON (name, type, acquisition_method, image_url) VALUES ('原型：信玄V型', '冲锋枪', '没有前置任务，游戏一开始就可以来拿，在河谷区，翻墙进入到荒坂工业园内（门口有传送点）找到667号集装箱（里边有颗地雷需要解除），进入集装箱最里边就可以得到了。', 'https://placehold.co/600x400/00b4eb/ffffff?text=%E4%BF%A1%E7%8E%84V%E5%9E%8B');
INSERT INTO WEAPON (name, type, acquisition_method, image_url) VALUES ('四季宝', '手枪', '没有前置任务，游戏一开始就可以来拿，在丽景区射击场传送点北边的一处小巷子里。这把枪有个特点，会随着使用者的等级提升而成长。另外，触发手枪的剧情（需要等到主线进展到黑梦）之后选择锁定头部模式或者锁定膝盖模式都行，干掉50个人之后会触发对话切换到另外一个模式，然后再干掉50个敌人，触发任务「随身小宝」，这时候选择不把枪还给主人，就可以放到自己家里的储藏处了，可以在家里的墙上可以看到它。', 'https://placehold.co/600x400/ff0055/ffffff?text=%E5%9B%9B%E5%AD%A3%E5%AE%9D');
INSERT INTO WEAPON (name, type, acquisition_method, image_url) VALUES ('混乱', '手枪', '主线任务「拿货」去交易小平头的时候，跟罗伊斯的对话选择「你真想让我再掏一次钱？」「要不要给我们打个折？」，之后干掉罗伊斯就可以从他身上拿到这把武器。一旦错过就拿不到了！另外注意这把武器跟另一个不朽武器「大木大木」是冲突的，那个武器要求小平头的交易和平解决才行。', 'https://placehold.co/600x400/7300e6/ffffff?text=%E6%B7%B7%E4%B9%B1');
INSERT INTO WEAPON (name, type, acquisition_method, image_url) VALUES ('金刚', '手枪', '主线任务「偷货」进展到V和杰克进入荒坂塔顶楼的时候先别开保险箱，从荒坂赖宣的床头柜上可以得到这把武器。错过也可以通过卡bug的方式回来拿（比较麻烦）！', 'https://placehold.co/600x400/d4af37/000000?text=%E9%87%91%E5%88%9A');
INSERT INTO WEAPON (name, type, acquisition_method, image_url) VALUES ('觉', '武士刀', '主线任务「偷货」进展到V和杰克需要从荒坂塔顶楼逃跑的时候，先别从玻璃门出去，从荒坂三郎下来的室内楼梯一直上到停机坪，解决两个敌人后，可以从荒坂三郎的飞机上拿到。错过也可以通过卡bug的方式回来拿（比较麻烦）！', 'https://placehold.co/600x400/ff3e00/ffffff?text=%E8%A7%89');
INSERT INTO WEAPON (name, type, acquisition_method, image_url) VALUES ('金色狠婆娘', '手枪', '主线任务「偷货」的最后选择送杰克的遗体去哪儿的时候，选择「送他回家，送他去见他家人」。之后任务结束后会接到「再见杰克」的支线任务，把这个支线任务做完，最后从酒吧出来，把时间调整到24小时之后。然后回到酒吧，可以从杰克的祭坛上拿到这把武器。', 'https://placehold.co/600x400/fcee09/000000?text=%E9%87%91%E8%89%B2%E7%8B%A0%E5%A9%86%E5%A8%98');
INSERT INTO WEAPON (name, type, acquisition_method, image_url) VALUES ('退路', '手枪', '主线进展到「黑梦」，这时候去V活过来的地方，也就是「德克斯特·德肖恩」被竹村击毙的地方，从他的尸体上可以得到这个武器。位置在地图的右下角恶土，具体见图。', 'https://placehold.co/600x400/00b4eb/ffffff?text=%E9%80%80%E8%B7%AF');
INSERT INTO WEAPON (name, type, acquisition_method, image_url) VALUES ('源二郎', '手枪', '主线进展到「黑梦」，去北橡区的一所豪宅别墅里（挨着克里的别墅），潜入到书房可以在桌子上拿到这把武器。', 'https://placehold.co/600x400/ff0055/ffffff?text=%E6%BA%90%E4%BA%8C%E9%83%8E');
INSERT INTO WEAPON (name, type, acquisition_method, image_url) VALUES ('手术刀', '武士刀', '主线进展到「黑梦」，到来生酒吧找到丹尼斯接到支线任务「东瀛第一等」，做完这个任务之后可以在旁边捡到这个武器。', 'https://placehold.co/600x400/7300e6/ffffff?text=%E6%89%8B%E6%9C%AF%E5%88%80');
INSERT INTO WEAPON (name, type, acquisition_method, image_url) VALUES ('零五', '狙击枪', '主线进展到「黑梦」，可以接到支线任务「黑拳：河谷区」，做完这个任务后继续跟巴克对话，然后干掉巴克，就可以从他一开始站的位置旁边得到这把武器。', 'https://placehold.co/600x400/d4af37/000000?text=%E9%9B%B6%E4%BA%94');
INSERT INTO WEAPON (name, type, acquisition_method, image_url) VALUES ('分则能成', '突击步枪', '主线进展到「黑梦」，到科罗纳多农场的「科罗纳多农场东」传送点，就在传送点旁边的黄色问号可以接到支线任务「信仰是一颗子弹」，可以堂堂正正的做完这个任务拿到武器，也可以直接掏枪干掉这波人，同样可以拿到武器。', 'https://placehold.co/600x400/ff3e00/ffffff?text=%E5%88%86%E5%88%99%E8%83%BD%E6%88%90');
INSERT INTO WEAPON (name, type, acquisition_method, image_url) VALUES ('主权', '双管霰弹枪', '主线进展到「黑梦」，来到日本街地区的「丝绸西路」传送点，在东北方向有个骷髅头，干掉这里的头头可以得到「主权」的图纸。', 'https://placehold.co/600x400/fcee09/000000?text=%E4%B8%BB%E6%9D%83');
INSERT INTO WEAPON (name, type, acquisition_method, image_url) VALUES ('电锯', '冲锋枪', '主线进展到「黑梦」，来到北区「潘兴街」传送点，北面有个骷髅头，干掉这里的头头可以得到「电锯」的图纸。', 'https://placehold.co/600x400/00b4eb/ffffff?text=%E7%94%B5%E9%94%AF');
INSERT INTO WEAPON (name, type, acquisition_method, image_url) VALUES ('诗篇11:6', '突击步枪', '主线进展到「黑梦」，来到北区「码头」传送点，南面有个骷髅头，干掉这里的头头可以得到「诗篇11:6」的图纸。', 'https://placehold.co/600x400/ff0055/ffffff?text=%E8%AF%97%E7%AF%8711%3A6');
INSERT INTO WEAPON (name, type, acquisition_method, image_url) VALUES ('应龙', '冲锋枪', '主线进展到「黑梦」，来到谷地区「文图拉街和天际线」传送点，西南方向有个骷髅头，干掉这里的头头「丹泽尔·克赖尔」可以得到「应龙」的图纸。', 'https://placehold.co/600x400/7300e6/ffffff?text=%E5%BA%94%E9%BE%99');
INSERT INTO WEAPON (name, type, acquisition_method, image_url) VALUES ('同志的铁锤', '左轮手枪', '主线进展到「黑梦」，来到河谷区「红泥酒吧」传送点，东南方向有个骷髅头，干掉这里的头头「达瑞斯·麦尔斯」可以得到「同志的铁锤」的图纸。', 'https://placehold.co/600x400/d4af37/000000?text=%E5%90%8C%E5%BF%97%E7%9A%84%E9%93%81%E9%94%A4');
INSERT INTO WEAPON (name, type, acquisition_method, image_url) VALUES ('你要战便来战', '突击步枪', '主线进展到「黑梦」，来到太平州西风庄园的南部「西风公寓」传送点，西南方向有个骷髅头，干掉这里的头头「安东·科列夫」可以得到「你要战便来战」的图纸。', 'https://placehold.co/600x400/ff3e00/ffffff?text=%E4%BD%A0%E8%A6%81%E6%88%98%E4%BE%BF%E6%9D%A5%E6%88%98');
INSERT INTO WEAPON (name, type, acquisition_method, image_url) VALUES ('突破', '狙击步枪', '主线进展到「黑梦」，来到科罗纳多农场南面的「科罗纳多农场南」传送点，东南方向有个骷髅头，干掉这里的头头「奥尔加·伊丽莎白」可以得到「突破」的图纸。', 'https://placehold.co/600x400/fcee09/000000?text=%E7%AA%81%E7%A0%B4');
INSERT INTO WEAPON (name, type, acquisition_method, image_url) VALUES ('刽子手', '霰弹枪', '主线进展到「黑梦」，来到北橡区东面的「荒坂地产」传送点，西北方向有个骷髅头，干掉这里最大的机器人BOSS「军用科技机甲」可以得到「刽子手」的图纸。', 'https://placehold.co/600x400/00b4eb/ffffff?text=%E5%88%BD%E5%AD%90%E6%89%8B');
INSERT INTO WEAPON (name, type, acquisition_method, image_url) VALUES ('藤津伪器', '单手棍棒', '触发支线「穿皮草的维纳斯」，和「梅瑞德斯」发生OOXX剧情之后从床上拿到。如何触发支线请参阅本站的妹子攻略。', 'https://placehold.co/600x400/ff0055/ffffff?text=%E8%97%A4%E6%B4%A5%E4%BC%AA%E5%99%A8');
INSERT INTO WEAPON (name, type, acquisition_method, image_url) VALUES ('丽姿', '手枪', '主线进展到「高级动物」，到丽姿酒吧去找朱迪的时候。1.5之前的版本在朱迪工作室门口的桌子上，1.5之后的版本在朱迪工作室内部包间的桌子上。', 'https://placehold.co/600x400/7300e6/ffffff?text=%E4%B8%BD%E5%A7%BF');
INSERT INTO WEAPON (name, type, acquisition_method, image_url) VALUES ('鸡尾酒棒', '武士刀', '主线进展到「高级动物」，到云顶去找艾芙琳，当要面对木头人的时候先别去找木头人对话，在2楼的一个化妆间里可以找到这把武器。就在艾芙琳的外套旁边。', 'https://placehold.co/600x400/d4af37/000000?text=%E9%B8%A1%E5%B0%BE%E9%85%92%E6%A3%92');
INSERT INTO WEAPON (name, type, acquisition_method, image_url) VALUES ('水腹蛇', '单手棍棒', '主线进展到「垃圾场」，到诊所去找指头哥的时候进屋先别跟指头哥对话，到里屋的床上可以得到这个武器。', 'https://placehold.co/600x400/ff3e00/ffffff?text=%E6%B0%B4%E8%85%B9%E8%9B%87');
INSERT INTO WEAPON (name, type, acquisition_method, image_url) VALUES ('寡妇制造者', '精准步枪', '主线进展到「死城」，夺回帕南的车之后，选择【好吧。他们躲在哪儿了？】，帮助帕南灭掉纳什，从纳什尸体上得到这把武器。', 'https://placehold.co/600x400/fcee09/000000?text=%E5%AF%A1%E5%A6%87%E5%88%B6%E9%80%A0%E8%80%85');
INSERT INTO WEAPON (name, type, acquisition_method, image_url) VALUES ('爪磨', '武士刀', '朱迪的支线进展到「千人千面」的时候杀入弘美的顶层公寓，就在舞子所在的房间茶几上，需要战斗结束后才能拿。', 'https://placehold.co/600x400/00b4eb/ffffff?text=%E7%88%AA%E7%A3%A8');
INSERT INTO WEAPON (name, type, acquisition_method, image_url) VALUES ('力大砖飞', '冲锋枪', '帕南的第一个支线任务「冲破风暴」去乱刀会营地救索尔的时候，干掉营地门口附近穿皮衣的杂兵，可从他身上得到这个武器。', 'https://placehold.co/600x400/ff0055/ffffff?text=%E5%8A%9B%E5%A4%A7%E7%A0%96%E9%A3%9E');
INSERT INTO WEAPON (name, type, acquisition_method, image_url) VALUES ('守望', '狙击步枪', '帕南的第一个支线任务「冲破风暴」，任务最后可以从帕南旁边的摩托车那里得到这个武器。', 'https://placehold.co/600x400/7300e6/ffffff?text=%E5%AE%88%E6%9C%9B');
INSERT INTO WEAPON (name, type, acquisition_method, image_url) VALUES ('毒刺', '小刀', '在帕南的营地附近有个黄色叹号，过去跟米契对话触发任务「飞翔鸟」，做完任务，武器就在你的背包里了', 'https://placehold.co/600x400/d4af37/000000?text=%E6%AF%92%E5%88%BA');
INSERT INTO WEAPON (name, type, acquisition_method, image_url) VALUES ('小叮当', '单手棍棒', '主线做到一定程度之后，市长候选人会给你派一个任务「向法律宣战」，完成任务，选项无影响。完成后跳时间可以接到后续任务「猎杀」，和瑞弗一起去寻找他的侄子。进入农场中间的房子（不是仓库）房子二楼右手第一个房间最里面有一个没有开的门，电源在桌子下面，打开后可以拾取到小叮当。武器只能在任务中获得，任务完成后房屋会无法进入。', 'https://placehold.co/600x400/ff3e00/ffffff?text=%E5%B0%8F%E5%8F%AE%E5%BD%93');
INSERT INTO WEAPON (name, type, acquisition_method, image_url) VALUES ('真探', '左轮手枪', '主线做到一定程度之后，市长候选人会给你派一个任务「向法律宣战」，完成任务，选项无影响。完成后跳时间可以接到后续任务「猎杀」。最后接到任务「爱似河」。瑞弗邀请你去他家聚餐。聚餐过程中和他家里人刷刷好感，然后他会约你单独聊聊，顺着他一直说就行，他会送你这把左轮。（不一定要跟他OOXX）', 'https://placehold.co/600x400/fcee09/000000?text=%E7%9C%9F%E6%8E%A2');
INSERT INTO WEAPON (name, type, acquisition_method, image_url) VALUES ('尽忠丸', '武士刀', '主线剧情进展到「虎口拔牙」，会触发跟小田的BOSS战，击败他就可以得到这把武器了。', 'https://placehold.co/600x400/00b4eb/ffffff?text=%E5%B0%BD%E5%BF%A0%E4%B8%B8');
INSERT INTO WEAPON (name, type, acquisition_method, image_url) VALUES ('莫克斯', '霰弹枪', '把朱迪的所有支线都做完，之后会不定期收到朱迪发来的短信，最后一个短信标题是「我有东西给你」，收到这个短信就可以去她家，在厨房的桌子上拿到这把武器。', 'https://placehold.co/600x400/ff0055/ffffff?text=%E8%8E%AB%E5%85%8B%E6%96%AF');
INSERT INTO WEAPON (name, type, acquisition_method, image_url) VALUES ('马洛里安武器公司3516', '手枪', '接到最终任务「夜曲OP.55 N.1」的同时可以会接到罗格的支线任务「插管」，把「插管」做完就可以得到这把银手的专属武器。', 'https://placehold.co/600x400/7300e6/ffffff?text=%E9%A9%AC%E6%B4%9B%E9%87%8C%E5%AE%893516');
INSERT INTO WEAPON (name, type, acquisition_method, image_url) VALUES ('镀金棒球棍', '双手棍棒', '罗格的支线任务「插管」任务一直往后做，做到后面接到任务「二次冲突」解决完死亡之舞的事儿会去丹妮的别墅会和，她跟亨利闹腾完，可以在水池里捡到这个武器，提前来捡不到。', 'https://placehold.co/600x400/d4af37/000000?text=%E9%95%80%E9%87%91%E6%A3%92%E7%90%83%E6%A3%8D');
INSERT INTO WEAPON (name, type, acquisition_method, image_url) VALUES ('大天使', '左轮手枪', '罗格的支线任务「插管」任务一直往后做，做到最后一个任务「至尊」，演唱会结束后克里会给你这把枪。至此，家里的展示墙就全部凑齐了。', 'https://placehold.co/600x400/ff3e00/ffffff?text=%E5%A4%A7%E5%A4%A9%E4%BD%BF');
INSERT INTO WEAPON (name, type, acquisition_method, image_url) VALUES ('偏见', '突击步枪', '最后选择罗格结局，之后在任务「丧钟为谁而鸣」中，罗格让你拿装备去干一场的时候，从旁边的桌子上可以拿到这个武器。', 'https://placehold.co/600x400/fcee09/000000?text=%E5%81%8F%E8%A7%81');
INSERT INTO WEAPON (name, type, acquisition_method, image_url) VALUES ('看门人的铲子', '锤子', '最后选择罗格结局，之后在任务「敲响天堂之门」中，也就是和罗格一起降落到荒坂塔楼顶丛林的时候，可以从罗格身边拿到这个武器。', 'https://placehold.co/600x400/00b4eb/ffffff?text=%E7%9C%8B%E9%97%A8%E4%BA%BA%E7%9A%84%E9%93%B2%E5%AD%90');
INSERT INTO WEAPON (name, type, acquisition_method, image_url) VALUES ('骄傲', '手枪', '最后选择罗格结局，之后在最终跟重锤的决战之后，可以从地上捡起这把武器。', 'https://placehold.co/600x400/ff0055/ffffff?text=%E9%AA%84%E5%82%B2');
INSERT INTO WEAPON (name, type, acquisition_method, image_url) VALUES ('八星铳', '霰弹枪', '通关之后选择继续玩（之前跟重锤的决战拿到他身上的钥匙），之后去北区的「埃布尼克码头」传送点，到之前做「插管」任务时候去过的码头上，还记得有个之前怎么也进不去的门吗？现在就可以进去了，进去之后可以拿到这最后一把霰弹枪「八星铳」。', 'https://placehold.co/600x400/7300e6/ffffff?text=%E5%85%AB%E6%98%9F%E9%93%B3');
INSERT INTO WEAPON (name, type, acquisition_method, image_url) VALUES ('大赦', '左轮手枪', '最后选择帕南结局，在「风暴前夕」任务里晚上在营地可以找到「卡西迪」玩枪打瓶子的游戏，游戏胜利后可以从他那里得到这把枪。（游戏有点难，而且不能重复挑战，提前存个档吧）', 'https://placehold.co/600x400/d4af37/000000?text=%E5%A4%A7%E8%B5%A6'); 